  LinkVerticalCurve,
  LinkRadialCurve,
  LinkHorizontalLine,
  LinkVerticalLine,
  LinkRadialLine
} from '@vx/shape'

const data = {
  name: '',
  children: [
    {
      name: 'J A',
      id: 1,
      gender: 'Female',
      hasParnter: true,
      imageUrl: '/avatar/gravator.jpg'
