      name: 'Mr. J A',
      id: 2,
      partnerId: 1,
      noParent: true,
      children: [
        {
          name: 'P A',
          id: 3,
          gender: 'Female',
          isSource: true,
          hasParnter: true
        },
        {
          name: 'MK N A',
          id: 5,
          partnerId: 3,
          gender: 'Male',
          noParent: true,
          children: [
            {
              name: 'B K',
              id: 100,
              hasParnter: true
            },
            {
              name: 'D MM',
              id: 102,
              partnerId: 100,
              gender: 'Female',
              noParent: true,
              children: [{ name: 'D MM', gender: 'Female' }]
            },
            {
              name: 'MM D',
              id: 102,
              partnerId: 100,
              gender: 'Female',
              noParent: true,
              children: [{ name: 'B MM' }]
